settings.markdownCss:
.tb-markdown-view .tb-progress-cover, .tb-markdown-view .mat-drawer-container {
    background-color: #fff;
}

.tb-markdown-view div, .tb-markdown-view p {
    line-height: normal;
}

.market-layout {
    position: relative;
}

.market-title {
    position: absolute;
    top: 10px;
    left: 10px;
    z-index: 2;
}

config.widgetCss:
.tb-widget-container .tb-widget .tb-multiple-input {
    background-color: #FAFAFA;
    border-radius: 4px;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle {
    margin-top: 15px !important;
    margin-bottom: 0 !important;
    padding-right: 30px;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-form-field > label .mat-icon {
    margin-left: 0;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-form-field .label-wrapper {
    font-weight: bold;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch {
    width: 42px;
    margin-left: 8px;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch .mdc-switch__track {
    border-radius: 16px;
    height: 24px;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch .mdc-switch__handle-track {
    width: calc(100% - 24px);
    left: 2px;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch .mdc-switch__handle {
    border-radius: 10px;
    width: 20px;
    height: 20px;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch .mdc-switch__ripple {
    width: 40px;
    height: 40px;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch .mdc-switch__icons {
    display: none;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch.mdc-switch--unselected:enabled .mdc-switch__handle::after {
    background: #fafafa;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch.mdc-switch--selected:enabled .mdc-switch__handle::after {
    background: #fafafa;
}

.tb-widget-container .tb-widget .tb-multiple-input .mat-mdc-slide-toggle .mdc-switch .mdc-switch__track::after {
    background: #F2994A;
}

.tb-widget-container .tb-widget .leaflet-control-zoom {
    display: none;
}
